INSERT INTO `script_texts` VALUES (-1586007, 'My scepter will once again become whole!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1586006, 'Shal myrinan ishnu daldorah...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1586005, 'Together, the two parts shall become one, once again.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1586004, 'Read this tome I have placed before you, and speak the words aloud.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1586003, 'Please do as I instruct you.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1586002, 'For so long I have drifted in my cursed form. You have freed me... Your hard work shall be repaid.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1586001, 'You wish to learn of the stone? Follow me.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
