INSERT INTO `script_texts` VALUES (-1587013, 'Remulos, I am being drawn back... Tyrande... send her my love... Tell her I am safe... Tell her... Tell her I will return... Farewell...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Malfurion - SAY_5');
INSERT INTO `script_texts` VALUES (-1587012, 'Cenarius fights at my side. Illidan sits atop his throne in Outland brooding. I\'m afraid that the loss to Arthas proved to be his breaking point. Madness has embraced him, Remulos. He replays the events in his mind a thousand times per day, but in his mind, he is the victor and Arthas is utterly defeated. He is too far gone, old friend. I fear that the time may soon come that our bond is tested and it will not be as it was at the Well in Zin-Azshari.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Malfurion - SAY_4');
INSERT INTO `script_texts` VALUES (-1587011, 'Aye Remulos, prepare the mortal races.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Malfurion - SAY_3');
INSERT INTO `script_texts` VALUES (-1587010, 'I fear for the worst, old friend. Within the Dream we fight a new foe, born of an ancient evil. Ysera\'\'s noble brood has fallen victim to the old whisperings. It seems as if the Nightmare has broken through the realm to find a new host on Azeroth.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Malfurion - SAY_2');
INSERT INTO `script_texts` VALUES (-1587009, 'Remulos, old friend. It is good to see you once more. I knew the message would find its way to you one way or another.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Malfurion - SAY_1');
INSERT INTO `script_texts` VALUES (-1587008, 'Let us return to the grove, mortal.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Keeper Remulos - SAY_8');
INSERT INTO `script_texts` VALUES (-1587007, 'Farewell, old friend... Farewell...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Keeper Remulos - SAY_7');
INSERT INTO `script_texts` VALUES (-1587006, 'You have been gone too long, Malfurion. Peace between the Children of Azeroth has become tenuous at best. What of my father? Of your brother? Have you any news?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 6, 'Keeper Remulos - SAY_6');
INSERT INTO `script_texts` VALUES (-1587005, 'I sensed as much, Malfurion. Dark days loom ahead.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Keeper Remulos - SAY_5');
INSERT INTO `script_texts` VALUES (-1587004, 'It was shrouded in nightmares, Malfurion. What is happening in the Dream? What could cause such atrocities?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 6, 'Keeper Remulos - SAY_4');
INSERT INTO `script_texts` VALUES (-1587003, 'Malfurion!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 5, 'Keeper Remulos - SAY_3');
INSERT INTO `script_texts` VALUES (-1587002, 'Stand near me, $N. I will protect you should anything go wrong.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Keeper Remulos - SAY_2');
INSERT INTO `script_texts` VALUES (-1587001, 'Come, $N. The lake is around the bend.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Keeper Remulos - SAY_1');
